export function Home() {
  const theme = useTheme();
  const [filters, setFilters] = useState<FilterData | null>(null);

  const filterBottomSheetRef = useRef<FilterBottomSheetRefProps>(null);

  function handleOpenFiltersModal() {
    filterBottomSheetRef.current?.open();
  }

  function handleApplyFilters(filters: FilterData) {
    setFilters(filters);
  }

  return (
    <Container>
      <Header>
        <ProfileImage
          source={{
            uri: "https://github.com/MiguelASFerreira.png",
          }}
        />

        <ProfileContent>
          <ProfileName>Olá, Miguel!</ProfileName>
          <ProfileView>
            <ProfileViewText>Ver perfil</ProfileViewText>
            <ArrowRight size={16} color={theme.COLORS.ORANGE_BASE} />
          </ProfileView>
        </ProfileContent>
      </Header>

      <SearchTitle>Explore produtos</SearchTitle>
      <SearchContent>
        <InputWrapper>
          <Input placeholder="Pesquisar" LeftIcon={Search} />
        </InputWrapper>

        <FilterButton onPress={handleOpenFiltersModal}>
          <Funnel size={20} color={theme.COLORS.ORANGE_BASE} />
        </FilterButton>
      </SearchContent>

      <FlatList
        data={data}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => <ProductCard product={item} />}
        contentContainerStyle={{ paddingVertical: 16 }}
        numColumns={2}
        columnWrapperStyle={{
          gap: "8%",
          width: "100%",
          marginBottom: 8,
          justifyContent: "space-between",
        }}
        showsVerticalScrollIndicator={false}
      />

      <FilterBottomSheet
        ref={filterBottomSheetRef}
        onApplyFilters={handleApplyFilters}
      />
    </Container>
  );
}
